set tSC = $$$OK try { set tSrcId = +..inputSource set tDomainId = ..reportDomain set tDomainIdCause = ..causeDomain
// first kill existing dictionary set tDictId = ##class(%iKnow.Matching.DictionaryAPI).GetDictionaryId(tDomainId, ..#IKNOWDICTNAME) set:tDictId>0 tSC = ##class(%iKnow.Matching.DictionaryAPI).DropDictionary(tDomainId, tDictId) quit:$$$ISERR(tSC) set tDictIdCause = ##class(%iKnow.Matching.DictionaryAPI).GetDictionaryId(tDomainIdCause, ..#IKNOWDICTNAME) set:tDictIdCause>0 tSC = ##class(%iKnow.Matching.DictionaryAPI).DropDictionary(tDomainIdCause, tDictIdCause) quit:$$$ISERR(tSC)
// now create a new one, containing all entities and CRCs of the current source // NOTE: this is a very crude approach. refining this to all entities that are dominant // in the input text and/or not too common in the overall domain (such as "pilot" etc), // could improve the results. set ..dictId = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionary(tDomainId, ..#IKNOWDICTNAME) set tDictIdCause = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionary(tDomainIdCause, ..#IKNOWDICTNAME)
set tSC = ##class(%iKnow.Queries.EntityAPI).GetBySource(.tEntities, tDomainId, tSrcId, 0, 0) quit:$$$ISERR(tSC) set i="" for { set i = $order(tEntities(i),1,tData) quit:i=""
set tSC = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionaryItemAndTerm(tDomainId, ..dictId, $li(tData,2), ":entity:"_$li(tData,1)_":"_$li(tData,2)) quit:$$$ISERR(tSC) set tSC = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionaryItemAndTerm(tDomainIdCause, tDictIdCause, $li(tData,2), ":entity:"_$li(tData,1)_":"_$li(tData,2)) quit:$$$ISERR(tSC) } quit:$$$ISERR(tSC)
set tSC = ##class(%iKnow.Queries.CrcAPI).GetBySource(.tCrcs, tDomainId, tSrcId, 0, 0) quit:$$$ISERR(tSC) set i="" for { set i = $order(tCrcs(i),1,tData) quit:i=""
set tSC = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionaryItemAndTerm(tDomainId, ..dictId, $lts($li(tData,2,4)," "), ":crc:"_$lts($li(tData,1,4),":")) quit:$$$ISERR(tSC) set tSC = ##class(%iKnow.Matching.DictionaryAPI).CreateDictionaryItemAndTerm(tDomainIdCause, tDictIdCause, $lts($li(tData,2,4)," "), ":crc:"_$lts($li(tData,1,4),":")) quit:$$$ISERR(tSC) } quit:$$$ISERR(tSC)
// now match all sources in the reports domain against this new dictionary set tSC = ##class(%iKnow.Matching.MatchingAPI).MatchSources(tDomainId, ..filter, $lb(..dictId)) quit:$$$ISERR(tSC)
} catch (ex) { set tSC = ex.AsStatus() } quit tSC

