// prepare main event statements set tEventResultSet = ##class(%ResultSet).%New("%DynamicQueryGW:SQLGW") set tSQLPref = "SELECT *, -1 As FakeOrder FROM events WHERE ev_id = '"_$lts(pPrefList,"' OR ev_id = '")_"'" set tSQL = "SELECT *, (ev_time*ev_time) MOD 79 As FakeOrder FROM events WHERE " _ $s(pWhereClause="":"ev_year >= 2000", 1:pWhereClause) set tSQL = "SELECT * FROM ("_tSQLPref_") UNION ("_tSQL_") ORDER BY FakeOrder" set tCountSQL = "SELECT COUNT(*) FROM ("_tSQL_")" set tSC = tEventResultSet.Prepare(tCountSQL,, tConnection) quit:$$$ISERR(tSC) set tSC = tEventResultSet.Execute() quit:$$$ISERR(tSC) do tEventResultSet.Next() set tMaxCount = tEventResultSet.GetData(1) w:pVerbose !,"Loading ",tMaxCount," events from DSN '",pDSN,"'" do tEventResultSet.Close() set tSC = tEventResultSet.Prepare(tSQL,, tConnection) quit:$$$ISERR(tSC) set tSC = tEventResultSet.Execute() quit:$$$ISERR(tSC)
// prepare other statements set tNarrativeResultSet = ##class(%ResultSet).%New("%DynamicQueryGW:SQLGW") set tAircraftResultSet = ##class(%ResultSet).%New("%DynamicQueryGW:SQLGW") set tCrewResultSet = ##class(%ResultSet).%New("%DynamicQueryGW:SQLGW")
set tCount = 0, tSkipCount = 0, tUpdateCount = 0 while tEventResultSet.Next() {
