// load first narrative for this event set tSC = tNarrativeResultSet.Prepare("SELECT * FROM narratives WHERE ev_id = '"_tEventId_"' AND Aircraft_Key = 1",, tConnection) quit:$$$ISERR(tSC) set tSC = tNarrativeResultSet.Execute() quit:$$$ISERR(tSC) if tNarrativeResultSet.Next() { set tString = $zstrip(tNarrativeResultSet.Get("narr_cause"), "<>W") set tString = $translate(tString, $c(8216,8219,8217,8220,8223,8221,8211,243,8212,8482,8226), "'''""""""-o- -") set tString = $replace($replace(tString,$c(176)," degrees"),$c(8230),"...") set tEvent.NarrativeCause = $zconvert(tString,"O","UTF8") set tString = $zstrip(tNarrativeResultSet.Get("narr_accp"), "<>W") set tString = $translate(tString, $c(8216,8219,8217,8220,8223,8221,8211,243,8212,8482,8226), "'''""""""-o- -") set tString = $replace($replace(tString,$c(176)," degrees"),$c(8230),"...") set tEvent.NarrativeFull = $zconvert(tString,"O","UTF8") set tString = $zstrip(tNarrativeResultSet.Get("narr_accf"), "<>W") set tString = $translate(tString, $c(8216,8219,8217,8220,8223,8221,8211,243,8212,8482,8226), "'''""""""-o- -") set tString = $replace($replace(tString,$c(176)," degrees"),$c(8230),"...") set tEvent.NarrativeSummary = $zconvert(tString,"O","UTF8") } do tNarrativeResultSet.Close()
// verify pSkipWhenEmpty elements set ptr=0, tSkip=0 while $listnext(tEventProps, ptr, tProp) { if ($property(tEvent, tProp)="") { set tSkip=$i(tSkipCount) quit } } continue:tSkip
