// skip if no Crew entries left continue:'tAircraft.Crew.Count() } do tAircraftResultSet.Close()
// skip if no Aircraft entries left if ('tEvent.Aircraft.Count()) { set tSkipCount = tSkipCount+1 continue }
set tSC = tEvent.%Save() quit:$$$ISERR(tSC)
set x = $i(tCount)
if '(tCount#100) { w:pVerbose !,"Processed ",tCount," out of ",tMaxCount," events... ",$s(tUpdateCount:"(including "_tUpdateCount_" updates) ", 1:"") w:pVerbose&&tSkipCount " - skipped ",tSkipCount," because of missing data" }
quit:pMaxRecords&&(pMaxRecords<=tCount) } quit:$$$ISERR(tSC) do tEventResultSet.Close()
w:pVerbose !!,"Disconnecting from DSN '",pDSN,"'" set tSC = tConnection.Disconnect() quit:$$$ISERR(tSC)
w:pVerbose !!,"Done",!,"Loaded ",tCount," events ",$s(tUpdateCount:"(including "_tUpdateCount_" updates) ", 1:"") w:pVerbose&&tSkipCount " - skipped ",tSkipCount," because of missing data" w:pVerbose !
} catch (ex) { set tSC = ex.AsStatus() } quit tSC

 If (%request.Data("Action",1) = "R") {
	// Delete the cookie by setting the expiration date to yesterday
	Set Expires = +$H - 1
	Set Expires = $ZD(Expires,11) _ ", " _ $ZD(Expires,2) _ " 00:00:00 GMT"
	Do %response.SetCookie("CacheCinemaLastCategory", "", Expires, "/")
 }
 Quit 1

 Write "<HTML>",!
