        //now set global to contain occurrence rates by gender and age
        Set fpiece=$ZSTRIP($Piece(value,"^",3),"*CW")    ; strip out line feed & white space
        Set mpiece=$ZSTRIP($Piece(value,"^",4),"*CW")    ; strip out line feed & white space

