Set tSC = $$$OK

// Compute additional values
Set tFilters = ..%filterValues

// compute recent history using query
If ((tFilters.Year'="")&&(tFilters.Year'="*")&&(tFilters.Year'="&[NOW]")) {
	// Take &[] off of Year value!
	Set tStartMonth = "Jan-"_$E(tFilters.Year,3,6)
