	quit:$$$ISERR(tSC)

	// translate sentence IDs to positions (Module 4, exercise 3)
	set tSource = ##class(%iKnow.Objects.Source).PKINDEXOpen(..Domain, ..SelectedSource)
	for tSentPos = 1:1:tSource.FirstSentenceId+tSource.SentenceCount {
