    do ##class(Sample.Person).%KillExtent()
    do ##class(Sample.Employee).%KillExtent()
    //do ##class(Sample.Vendor).%KillExtent() ; doesn't work with %CacheSQLStorage 

    Set companyCount= personCount \ 5
    If (companyCount < 1) {
        Set companyCount=1
        }

