var wh = zenPage.getNativeWindowHeight();

this.vX = 50;
this.vY = wh-50;
this.hX = ww-50;
this.hY = this.vY;
// get knob centers
this.vcX = 50;
this.vcY = wh-50;
this.hcX = ww-50;
this.hcY = this.vY;

this.vD = 0;
this.hD = 0;

var t=event.touches;
for (var i=t.length-1;i>=0;i--) {
	var pt = t[i];
	var px = pt.pageX;
	var py = pt.pageY;
	// if the touch was in the right area, get the starting point
	if (py>wh-100) {
		if (px<100) {
			this.vX = px;
			this.vY = py;
		}
		else if (px>ww-100) {
			this.hX = px;
			this.hY = py;
		}
	}
}

var ww = zenPage.getNativeWindowWidth();
var wh = zenPage.getNativeWindowHeight();

// initialize potential destination points
var vmX = -1;
var vmY = -1;
var hmX = -1;
var hmY = -1;

var t=event.touches;
for (var i=t.length-1;i>=0;i--) {
	var pt = t[i];
	var px = pt.pageX;
	var py = pt.pageY;
	// it the touch is in the right region, mark it as a destination
	if (py>wh-100) {
		if (px<100) {
			vmX = px;
			vmY = py;
		}
		else if (px>ww-100) {
			hmX = px;
			hmY = py;
		}
	}
}
if (vmX> -1) { // movement detected on the vertical control
	var dx = vmX-this.vX;
	var dy = vmY-this.vY;
	var d = dx*dx+dy*dy;
	// Figure out the major axis of motion
	if (Math.abs(dx)>Math.abs(dy)) dy=0;
	else dx=0;

	// Figure out which quandrant we started in
	var q = 1;
	if (this.vX<this.vcX) q++;
	if (this.vY>this.vcY) q+=2;

	var val = 0; // no motion yet
	if (d>5) { // enough motion to test
		if (q==1) {
			if (dx<0 || dy<0) val++; // ccw
			else if (dx>0 || dy>0) val--; // cw
		} else if (q==2) {
			if (dx<0 || dy>0) val++;
			else if (dx>0 || dy<0) val--;
		} else if (q==3) {
			if (dx>0 || dy<0) val++;
			else if (dx<0 || dy>0) val--;
		} else {
			if (dx>0 || dy>0) val++;
			else if (dx<0 || dy<0) val--;
		}
		if (val!=0) {
			this.vY = vmY;
			this.vX = vmX;
			this.deltaY -= val;
		}
	}
}
if (hmX> -1) { // movement detected on the horizontal control
	var dx = hmX-this.hX;
	var dy = hmY-this.hY;
	var d = dx*dx+dy*dy;
	// Figure out the major axis of motion
	if (Math.abs(dx)>Math.abs(dy)) dy=0;
	else dx=0;
	// Figure out which quandrant we started in
	var q = 1;
	if (this.hX<this.hcX) q++;
	if (this.hY>this.hcY) q+=2;

	var val = 0; // no motion yet
	if (d>5) { // enough motion to test
		if (q==1) {
			if (dx<0 || dy<0) val++; // ccw
			else if (dx>0 || dy>0) val--; // cw
		} else if (q==2) {
			if (dx<0 || dy>0) val++;
			else if (dx>0 || dy<0) val--;
		} else if (q==3) {
			if (dx>0 || dy<0) val++;
			else if (dx<0 || dy>0) val--;
		} else {
			if (dx>0 || dy>0) val++;
			else if (dx<0 || dy<0) val--;
		}
		if (val!=0) {
			this.hY = hmY;
			this.hX = hmX;
			this.deltaX -= val;
		}
	}
}

if (event.accelerationIncludingGravity.z > 10.5) {
