		if (bux<b.lx && bux>b.ux) { // bump to the right?
			this.x = b.lx;
			this.vx = -2*e*this.vx;	
		}
		else if (this.x+40>b.ux && this.x+40<b.lx){ // bump to left
			this.x = b.ux-40;
			this.vx = -2*e*this.vx;
		}
		if (this.y<b.ly && this.y>b.uy) { // bump down
			this.y = b.ly;
			this.vy = -2*e*this.vy;
		}
		else if (this.y+40>b.uy && this.y+40<b.ly) { // bump up
			this.y = b.uy-40;
			this.vy = -2*e*this.vy;
		}
	}
}
