// add to our group
this.svgGroup.appendChild(rect);

switch(property) {
case 'style':
	this[property] = value;
	var el = this.findSVGElement('rect');
	if (el) {
		el.setAttribute(property,value);
	}
	break;
case 'width':
case 'height':
	var el = this.findSVGElement('rect');
	if (el) {
		el.setAttribute(property,value);
	}
	return this.invokeSuper('setProperty',arguments);

default:
	return this.invokeSuper('setProperty',arguments);
}
return true;

