			if (final) {
				console.log('done');
				$.mobile.loading('hide');
				// change the document key to the data we just finished loading
				mainView.setDocumentKey('slow-loading',{});
				// update the layout
				mainView.updateLayout();
			}
		};
		$.mobile.loading('show');
		zenPage.getContent('data','slow-loading',{},false,notify);

		break;
}

