Set tSC = $$$OK
// Populate some test data if Sample.Person is empty If ($Data(^Sample.PersonD) < 10) { Do ##class(Sample.Person).Populate(20) }
If ((pProviderName = "data") && (pKey = "table-demo")) {
Set pResponse = ##class(%ZEN.proxyObject).%New() Set pResponse.caption = "HTML Table Test with ZEN Mojo"
Set pResponse.cols = ##class(%ListOfObjects).%New() Set col = ##class(%ZEN.proxyObject).%New() Set col.header = "Identifier" Set col.style = "background-color:lightblue;" Do pResponse.cols.Insert(col) Set col = ##class(%ZEN.proxyObject).%New() Set col.header = "Name" Set col.style = "background-color:#EEE;" Do pResponse.cols.Insert(col) Set col = ##class(%ZEN.proxyObject).%New() Set col.header = "D.of Birth" Set col.style = "background-color:lightblue;" Do pResponse.cols.Insert(col)
Set pResponse.rows = ##class(%ListOfObjects).%New()
Set rs = ##class(%SQL.Statement).%ExecDirect(.st, "SELECT TOP 20 SSN, Name, TO_CHAR(DOB, 'YYYY-MM-DD') AS DOB FROM Sample.Person ORDER BY Name")
While $IsObject(rs) && (rs.%Next()) { Set row = ##class(%ZEN.proxyObject).%New() Set row.ssn = rs.SSN Set row.name = rs.Name Set row.dob = rs.DOB Do pResponse.rows.Insert(row) } }
ElseIf ((pProviderName = "data") && (pKey = "form-demo")) {
Set pResponse = ##class(%ZEN.proxyObject).%New()
Set pResponse.options = ##class(%ListOfObjects).%New()
Set rs = ##class(%SQL.Statement).%ExecDirect(.st, "SELECT Name FROM Sample.Person ORDER BY Name")
While $IsObject(rs) && (rs.%Next()) { Set opt = ##class(%ZEN.proxyObject).%New() Set opt.name = rs.Name Do pResponse.options.Insert(opt) } }
ElseIf ((pProviderName = "data") && (pKey = "selected-person")) {
Set pResponse = ##class(%ZEN.proxyObject).%New()
Set rs = ##class(%SQL.Statement).%ExecDirect(.st, "SELECT ID, Name, TO_CHAR(DOB, 'YYYY-MM-DD') AS DOB, Age, NVL($LISTGET(FavoriteColors, 1), 'white') AS FavColor"_ " FROM Sample.Person WHERE Name = ?", pCriteria.name)
While $IsObject(rs) && (rs.%Next()) { Set pResponse.id = rs.ID Set pResponse.name = rs.Name Set pResponse.dob = rs.DOB Set pResponse.age = rs.Age // The HTML5 color input works only with hexadecimal color codes Set pResponse.favcolor = ..EncodeColor(rs.FavColor) } }
Quit tSC

var content = null;

switch (which) {
	case 'data':
		content = this.getData(key, criteria);
		break;
	case 'layout':
		content = this.getLayout(key, criteria);
		break;
}

return content;

