		set ^||temp(##class(%PopulateUtils).Name()) = ""
	}

	set key = $O(^||temp(""))
	while (key '= "") {
		set element = ##class(%ZEN.proxyObject).%New()
		set element.label=key
		do pObject.elements.Insert(element)
		set key = $O(^||temp(key))
	}

